mapper.update:
UPDATE tbl_review
SET REVIEW_TITLE=#{reviewTitle}, REVIEW_CONTENT=#{reviewContent}, REVIEW_DATE=now()
WHERE REVIEW_NUM=#{reviewNum}

mapper.select:
SELECT REVIEW_NUM, SELL_PRODUCT_NUM, USER_NUM, REVIEW_TITLE, REVIEW_CONTENT, REVIEW_DATE
FROM tbl_review
WHERE REVIEW_NUM = #{reviewNum}
